package com.rlk.mi.dao;import com.rlk.mi.entity.UserInfo;import org.apache.ibatis.annotations.Insert;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;import org.springframework.stereotype.Repository;import org.springframework.transaction.annotation.Transactional;import java.util.List;@Repository(value = "userInfoMapper")@Transactionalpublic interface UserInfoMapper {    @Insert("INSERT into userInfo (uid, type) VALUES (#{uid}, #{type})")    public void insert(UserInfo userInfo);    @Select("SELECT * from userInfo WHERE uid = #{uid}")    public UserInfo get(@Param("uid") String uid);    @Select("SELECT * from userInfo")    public List<UserInfo> getAll();}