package com.rlk.mi.dao;import com.rlk.mi.entity.UserReclaim;import org.apache.ibatis.annotations.Insert;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;import java.util.List;public interface UserReclaimMapper {    @Insert("INSERT into userReclaim (token, email, date) VALUES (#{token}, #{email}, #{date})")    public void insert(UserReclaim userReclaim);    @Select("SELECT * from userReclaim WHERE token = #{token}")    public UserReclaim get(@Param("token") String token);    @Select("SELECT * from userReclaim")    public List<UserReclaim> getAll();}