package com.rlk.mi.entity;import java.io.Serializable;public class UserAccount implements Serializable {    private static final long serialVersionUID = -1229489507416958633L;    private String email;    private String password;    private int status;    private int uid;    public void setEmail(String email){        this.email = email;    }    public String getEmail(){        return email;    }    public void setPassword(String password){        this.password = password;    }    public String getPassword(){        return password;    }    public void setStatus(int status){        this.status = status;    }    public int getStatus(){        return status;    }    public void setUid(int uid){        this.uid = uid;    }    public int getUid(){        return uid;    }}