package com.rlk.mi.entity;import org.springframework.stereotype.Repository;import java.io.Serializable;@Repository(value = "userInfo")public class UserInfo implements Serializable {    private static final long serialVersionUID = 4656847964188827202L;    private int uid;    private int type;    public void setUid(int uid){        this.uid = uid;    }    public int getUid(){        return uid;    }    public void setType(int type){        this.type = type;    }    public int getType(){        return type;    }}